import React from 'react';

function Slides({slides}) {
    const [index, setIndex] = React.useState(0);
    const [message, setMessage] = React.useState('');

    const handleChange = event => {
        setMessage(event.target.value);
        console.log('value is:', event.target.value);
    }
    return (
        <div>
            <div id="navigation" className="text-center">


                {/*<button
                    data-testid="button-prev"
                    disabled={index === 0}
                    onClick={() => setIndex(index - 1)}
                    className="small"
                >
                    Prev
                </button>*/}

            </div>
            <div id="slide" className="card text-center">
                <h1 data-testid="title">{slides[index]["title"]}</h1>
                {slides[index]["text"] && <p
                    data-testid="text">{slides[index]["text"]}
                </p>}
                {slides[index]["nome"] && <input
                    type={"text"}
                    id={slides[index]["nome"]}
                    name={slides[index]["nome"]}
                    onChange={handleChange}
                    value={message}
                />}
                {slides[index]["email"] && <input
                    type={"text"}
                    id={slides[index]["email"]}
                    name={slides[index]["email"]}
                    onChange={handleChange}
                    value={message}
                />}
                {slides[index]["button"] && <button
                    data-testid="button-next"
                    onClick={() => setIndex(index + 1)}
                    disabled={index === slides.length - 1}
                    className="small"

                > {slides[index]["button"]}
                </button>}
                {slides[index]["button1"] && <button
                    data-testid="button-next"
                    onClick={() => setIndex(index + 1)}
                    disabled={index === slides.length - 1}
                    className="small"

                > {slides[index]["button1"]}
                </button>}
                {slides[index]["button2"] && <button
                    data-testid="button-next"
                    onClick={() => setIndex(index + 1)}
                    disabled={index === slides.length - 1}
                    className="small"

                > {slides[index]["button2"]}
                </button>}
            </div>
        </div>
    );

}

export default Slides;
